import styled from 'styled-components';

export const Container = styled.div`
  color: #b2dd4c;
`; 

export const Cards = styled.div`
  width: 100%;
  max-width: 960px;
  margin: 50px auto;
  outline: none;
  display: flex;
  flex-wrap: wrap;


  @media screen and (max-width: 640px) {
    & {
      flex-direction: column;
      align-items: center; 
    }      
  }

  @media screen and (min-width: 640px) {
    & {
      padding: 0 100px;
      justify-content: space-between;

      &::after {
        content: "";
        flex: 0 0 auto;
      }
    }   
  }

  @media screen and (min-width: 960px) {
    & {
      padding: 0;
      justify-content: space-between;

      &::after {
        content: "";
        flex: 0 0 auto;
      }
    }   
  }

  a {
    display: flex;
    background: #fff;
    border-radius: 8px;
    border: solid 1px rgba(100,100,100,0.1);  
    max-width: 320px;
    height: 300px;
    box-shadow: 0px 1px rgba(100,100,100,0.1);
    margin: 0 20px;

    @media screen and (max-width: 960px) {
      & {
        margin-top: 30px;
      }      
    }

    @media screen and (max-width: 499px) {
      & {
        width: 90%;
      }      
    }
    @media screen and (min-width: 500px) {
      & {
        width: 280px;
      }   
    }

    flex-direction: column;
    text-decoration: none;
    align-items: center;
    margin-bottom: 60px;

    transition: transform 0.2s;
    /* mesmo elemento hover */
    &:hover {
      transform: translateY(-5px);
    }

    img {
      margin-top: 0;
      margin-bottom: 20px;
      border-top-left-radius: 8px;
      border-top-right-radius: 8px;
      width: 100%;
      height: 140px;
    }

    div {
      max-width:100%;
      min-width:230px;
      padding: 0 18px;

      span {
        font-size: 18px;
        color: #3d3d4d;
      }

      .data {
        flex-direction: row;
        font-size: 12px;
        color: #BBB;
        margin-top: 10px;
        line-height: 11px;
        padding:0;
        justify-content: center;

        span {
          margin-left: 10px;
          padding: 0 10px;
          border-left: solid 1px #EEE;
          color: #b2dd4c;
          font-weight: bold;
          font-size: 12px;
        }
      }

      .lead {
        font-size: 12px;
        line-height: 15px;
        color: #a8a8b3;
      }
    }

    svg {
      margin-left: auto;
      color: #cbcbd6;
    }
  }
`;
