import React from 'react';
import Link from 'next/link';

import Header from '../components/Header';

import { getAllPosts } from '../lib/api'
import Post from '../types/post'

import { GrInstagram, GrTwitter } from "react-icons/gr";
import { FaFacebookSquare } from "react-icons/fa";

import { Container, Cards, Social, DivPlane } from './index-styles';

type Props = {
  allPosts: Post[]
}

export const getStaticProps = async () => {
  const allPosts = getAllPosts([
    'title',
    'date',
    'slug',
    'author',
    'coverImage',
    'excerpt',
  ])

  return {
    props: { allPosts },
  }
}

export default function Home({ allPosts }: Props) {

  const post1 = allPosts[0]
  const post2 = allPosts[1]
  const post3 = allPosts[2]

  const posts = [
    {
      id: 1,
      uri: post1.coverImage,
      title: post1.title,
      date: post1.date, 
      link: `blog/posts/${post1.slug}.html`,
      category: "Blog",
      lead: "Testando o blog da Agência Digital Cuby criado em Gatsby"
    },
    {
      id: 2,
      uri: post2.coverImage,
      title: post2.title,
      date: post2.date, 
      link: `blog/posts/${post2.slug}.html`,
      category: "Blog",
      lead: "Testando o blog da Agência Digital Cuby criado em Gatsby"
    },
    {
      id: 3,
      uri: post3.coverImage,
      title: post3.title,
      date: post3.date, 
      link: `blog/posts/${post3.slug}`,
      category: "Blog",
      lead: "Testando o blog da Agência Digital Cuby criado em Gatsby"
    },

  ];

  return ( 
    <>
      <Header page="inicio" />
      <Container>
        <img  
          className="heroText"
          src="/assets/images/hero_text.svg"
        /> 
        <img
          className="heroBanner"
          src="/assets/images/hero_banner.svg"  
        />

        <h2>RECENTES NO BLOG</h2>
        <h5>Informações do mundo da tecnologia, cursos e dicas para desenvolvedores e empresários</h5>

        <Cards>
          {posts.map((post) => (
              <a 
                key={post.id} 
                href={post.link}
              >
                <img
                  style={{ maxWidth: 300 }}
                  src={post.uri}
                  alt={post.title}
                />
                <div>
                  <span>{post.title}</span>
                  <p className="data">{post.date}<span>Blog</span></p>
                  <p className="lead">{post.lead}</p>
                </div>
              </a>
          ))}
        </Cards>


        <Social>
          <a href="https://facebook.com/CubyDigital" target="_blank"><FaFacebookSquare size={30} color="#4267B2" /></a>
          <a href="https://instagram.com/CubyDigital" target="_blank"><GrInstagram size={28} color="#C13584" /></a>
          <a href="https://twitter.com/CubyDigital" target="_blank"><GrTwitter size={30} color="#1DA1F2" /></a>
        </Social>

        <DivPlane>
          <img className="planeImage" src="/assets/images/plane.svg" />
        </DivPlane>

      </Container>
    </>
  );
}
