import { FaFileExcel } from "react-icons/fa"

import DateFormater from './date-formater'

type Props = {
  name: string
  picture: string
  date: string
}

const Avatar = ({ name, picture, date }: Props) => {
  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row',
      marginBottom: 30
    }}>
      <img 
        src={picture} 
        alt={name} 
        style={{
          width: 50,
          borderRadius: '50%',
          marginRight: 20
        }}
      />
      <div>
        <div style={{ marginTop: 5, marginBottom: -5}}>{name}</div>
        <DateFormater dateString={date} />
      </div>
    </div>
  )
}

export default Avatar
