import styled from 'styled-components';

export const Container = styled.div`
  width: 95%;
  max-width: 960px;
  margin: auto;

  .heroText {
    width: 90%;
    max-width: 680px;
    display: block;
    position: relative;
    margin: 80px auto 1%;
    left: 3%;
    right: 3%;
  }

  .heroBanner {
    width: 100%;
    max-width: 960;
    display: block;
    margin: 0 auto 8%;
  }
`; 

export const DivPlane = styled.div`
  width: 100%;
  max-width: 960px;
  margin: 50px auto;
  display: flex;

  @media screen and (max-width: 800px) {
    & {
      display: block;
    }      
  }

  h3 {
    flex: 1;
    margin:auto;

    @media screen and (max-width: 1050px) {
      & {
        margin-left: 5%;
      }      
    }
  }

  .planeImage {
    position: absolute;
    right: -15px;
  }

`;
