import { FaFileExcel } from "react-icons/fa"

type Props = {
  name: string
  picture: string
}

const Avatar = ({ name, picture }: Props) => {
  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row'
    }}>
      <img 
        src={picture} 
        alt={name} 
        style={{
          width: 50,
          borderRadius: '50%'
        }}
      />
      <div>{name}</div>
    </div>
  )
}

export default Avatar
