import styled from 'styled-components';

export const Container = styled.div`
  width: 95%;
  max-width: 960px;
  margin: auto;

  .heroText {
    width: 90%;
    max-width: 680px;
    display: block;
    position: relative;
    margin: 80px auto 1%;
    left: 3%;
    right: 3%;
  }

  .heroBanner {
    width: 100%;
    max-width: 960;
    display: block;
    margin: 0 auto 8%;
  }
`; 

export const Social = styled.div`
  display: flex;
  flex-direction: row;
  max-width: 160px;
  margin: 60px auto 30px;
  text-align: center;
  align-items: center;
  justify-content: space-between;

  svg {
    display: block;
    margin:5px;
    transition: transform 0.2s;
    &:hover {
      transform: translateY(-3px);
    }
  }
`;

export const DivPlane = styled.div`
  width: 100%;
  max-width: 960px;
  margin-top:0;
  height: 40px;
  display: flex;
  flex: 1;

  .planeImage {
    position: absolute;
    right: -15px;
  }

`;
