import React from 'react';
import { useRouter } from 'next/router'
import Link from 'next/link';

import { parseISO, format } from 'date-fns'
import { ptBR } from 'date-fns/locale';

import Header from 'components/Header';

import { getAllPosts } from 'lib/api';
import Post from 'types/post';

import { Container, Cards } from './styles';

//** TO DO LIST: Menu flutuante com as categorias dos posts (curso, empreendedorismo, etc...) */

type Props = {
  allPosts: Post[]
}

const getStaticProps = async () => {
  const allPosts = getAllPosts([
    'title',
    'lead',
    'categories',
    'date',
    'slug',
    'author',
    'coverImage',
    'excerpt',
  ])

  return {
    props: { allPosts },
  }
}

const Blog = ({ allPosts }: Props) => {
  const router = useRouter();
  const { cat } = router.query;

  const posts = allPosts.slice(0,9)
  const morPosts = allPosts.slice(10)

  return ( 
    <>
      <Header page="blog" />
      <Container>

        <Cards>
            {posts.map((post) => (
                <Link key={post.slug} href={`blog/posts/${post.slug}`} passHref>
                  <a>
                    <img
                      style={{ maxWidth: 300 }}
                      src={post.coverImage}
                      alt={post.title}
                    />
                    <div>
                      <span>{post.title}</span>
                      <p className="data">{post.date}
                      {
                        post.categories.map(item => (
                          <span key={item}>{item}</span>
                        ))
                      }
                      </p>
                      <p className="lead">{post.lead}</p>
                    </div>
                  </a>
                </Link>
            ))}
          </Cards>

      </Container>
    </>
  )
};

export default Blog;
