import React, { useState } from 'react';
import { useRouter } from 'next/router'
import Link from 'next/link';

import { parseISO, format } from 'date-fns'
import { ptBR } from 'date-fns/locale';

import Header from 'components/Header';
import Footer from 'components/Footer';

import { getAllPosts } from 'lib/api';
import Post from 'types/post';

import { Container, Cards } from './styles';
import { CardMeta } from 'semantic-ui-react';

//** TO DO LIST: Menu flutuante com as categorias dos posts (curso, empreendedorismo, etc...) */

type Props = {
  allPosts: Post[]
}

export const getStaticProps = async () => {
  const allPosts = getAllPosts([
    'title',
    'lead',
    'categories',
    'date',
    'slug',
    'author',
    'coverImage',
    'excerpt',
  ])

  return {
    props: { allPosts },
  }
}

const Blog = ({ allPosts }: Props) => {
  const router = useRouter();
  const [cat, setCat] = useState('blog');

  if(router.query.cat) {
    setCat(router.query.cat.toString());
  }
  var posts = allPosts.slice(0,9)
  var morPosts = allPosts.slice(10)

  if(cat=='portfolio') {
    let filteredPosts = allPosts.filter(item => item.categories.includes(cat.toString()));
    let posts = filteredPosts.slice(0,9)
    let morPosts = filteredPosts.slice(10)
  }

  console.log(cat);
  return ( 
    <>
      <Header page={cat} />
      <Container>

        <Cards>
            {posts.map((post) => (
                <Link key={post.slug} href={`blog/posts/${post.slug}`} passHref>
                  <a>
                    <img
                      style={{ maxWidth: 300 }}
                      src={post.coverImage}
                      alt={post.title}
                    />
                    <div>
                      <span>{post.title}</span>
                      <p className="data">{format(parseISO(post.date), `d 'de' LLLL '/' yyyy`, { locale: ptBR })}
                      {
                        post.categories.map(item => (
                          <span key={item}>{item}</span>
                        ))
                      }
                      </p>
                      <p className="lead">{post.lead}</p>
                    </div>
                  </a>
                </Link>
            ))}
          </Cards>

      </Container>
      <Footer />
    </>
  )
};

export default Blog;
